import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../assets/css/app.css";
import { render } from "react-dom";
import * as firebase from "firebase";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import { db } from "../firebase/config";

export default class Register extends Component {
  state = { isSignedIn: false };

  uiConfig = {
    SignInflow: "popup",
    SignInOptions: [
      firebase.auth.EmailAuthProvider.PROVIDER_ID,
      firebase.auth.GoogleAuthProvider.PROVIDER_ID,
      firebase.auth.FacebookAuthProvider.PROVIDER_ID,
    ],
    callbacks: {
      signInSuccess: () => false,
    },
  };

  componentDidMount = () => {
    firebase.auth().onAuthStateChanged((user) => {
      console.log(user);
      this.setState({ isSignedIn: !!user }, () => {
        db.collection("users").add({
          login: user.displayName,
          followed_by: [],
          created_at: Date.now(),
          id: user.uid,
        });
      });
    });
  };

  render() {
    return (
      <div class="login_page">
        <div class="intro">
          <h2>Inscription</h2>
          <p>Inscrivez vous et rejoignez la communaut√© Dailyart!</p>
        </div>

        <div class="block_connexion">
          {this.state.isSignedIn ? (
            <div class="post_register">
              <h3> Inscription r√©ussie !</h3>
              <div class="bloc_infos">
                {/* <button onClick={()=> firebase.auth().signOut()}>Sign Out</button> */}
                <h1>Bienvenue, {firebase.auth().currentUser.displayName}</h1>
                <img
                  alt="profil picture"
                  src={firebase.auth().currentUser.photoURL}
                />
                <Link to={"/home"} class="arrow">
                  continuer
                </Link>
              </div>
            </div>
          ) : (
            <StyledFirebaseAuth
              uiConfig={this.uiConfig}
              firebaseAuth={firebase.auth()}
            />
          )}
        </div>

        {this.state.isSignedIn ? (
          <div></div>
        ) : (
          <div>
            <p class="accroche">
              D√©j√† un compte ?{" "}
              <Link to={"/login"} id="login">
                Connectez-vous!
              </Link>{" "}
            </p>
          </div>
        )}
      </div>
    );
  }
}

export class RegisterForm extends Component {
  render() {
    return (
      <div>
        <p> page du register form </p>
      </div>
    );
  }
}
