import React, { Component, useState } from "react";
import { Link } from "react-router-dom";
import "../assets/css/app.css";
import firebase from "firebase";
import { db, auth, storage } from "../firebase/config";

export default class Post extends Component {
  constructor(props) {
    super(props);
    this.fileInput = React.createRef();

    this.state = {
      isSignedIn: true,
      posts: [],
      image: null,
    };
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const { image } = this.state;
    console.log("submit");
    const imagesRef = storage.ref().child("images/" + image.name);
    try {
      const snapshot = await await imagesRef.put(image);
    } catch (error) {
      console.error(error);
    }
    const snapshot = await imagesRef
      .put(image)
      .then((snapshot) =>
        snapshot.ref
          .getDownloadURL()
          .then((url) => console.log(url))
          .catch((err) => console.error(err))
      )
      .catch((err) => console.error(err));
  };
  handleChange = (e) => {
    const file = this.fileInput.current.files[0];
    const url = window.URL.createObjectURL(file);
    this.setState({ image: file });
  };

  render() {
    const { posts, image } = this.state;
    return (
      <div className="home">
        <div className="header">
          <Link to="/profil">
            {" "}
            <img
              alt="profil picture"
              className="avatar"
              src="/logout/avatar.svg"
            />{" "}
          </Link>
          <Link to="/login">
            <button
              onClick={() => firebase.auth().signOut()}
              className="logout"
            ></button>
          </Link>
        </div>

        <div className="en-tete">
          <h2>Partagez</h2>
        </div>

        <form onSubmit={this.handleSubmit}>
          <input
            type="file"
            onChange={this.handleChange}
            ref={this.fileInput}
          />
          {image && <img src={window.URL.createObjectURL(image)} />}
          <input type="submit" value="Publier" />
        </form>
        <nav className="nav_menu">
          <Link to="/home">
            <div className="img1"></div>
          </Link>
          <Link to="/post">
            <div className="img2_b"></div>
          </Link>
          <Link to="/subscription">
            <div className="img3"></div>
          </Link>
          <Link to="/defi">
            <div className="img4"></div>
          </Link>
        </nav>
      </div>
    );
  }
}
