import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../assets/css/app.css";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import firebase from "firebase";
import firebaseConfig from "../firebase/config";
import { db, auth, storage } from "../firebase/config";
import Post from "./post";

export default class Home extends Component {
  state = {
    isSignedIn: true,
    posts: [],
  };

  componentDidMount() {
    db.collection("posts")
      .get()
      .then((snapshot) => {
        let posts = [];
        snapshot.docs.forEach((doc) => {
          let post = doc.data();
          post.id = doc.ref.id;
          console.log(post.author);
          if (post.author && post.challenge_ref) {
            db.doc(post.author.path)
              .get()
              .then((res) => {
                post.author = res.data();
                post.author.id = res.ref.id;
                db.doc(post.challenge_ref.path)
                  .get()
                  .then((res) => {
                    post.challenge_ref = res.data();
                    post.challenge_ref.id = res.id;
                  });
              })
              .catch((error) => console.log(error));
            posts.push(post);
          }
        });
        this.setState({ posts });
      })
      .catch((error) => console.log(error));
  }

  // FonctionnalitÃ© like
  isLiked(post) {
    const userId = localStorage.getItem("userId");
    const elmt = post.likes.findIndex((like) =>
      like.isEqual(db.doc(`users/${userId}`))
    );
    console.log(elmt);
    return elmt !== -1;
  }

  handleDislike = (e) => {
    e.preventDefault();
    const postId = e.target.id;
    const userId = localStorage.getItem("userId");
    let newPosts = [...this.state.posts];
    const idx = newPosts.findIndex((post) => post.id == postId);
    let newLikes = newPosts[idx].likes.filter(
      (like) => !like.isEqual(db.doc(`users/${userId}`))
    );
    db.doc(`posts/${postId}`)
      .update({
        likes: newLikes,
      })
      .then((postRef) => {
        newPosts[idx].likes = newLikes;
        this.setState({ posts: newPosts }, () => console.log(this.state.posts));
      });
  };

  handleLike = (e) => {
    e.preventDefault();
    const postId = e.target.id;
    const userId = localStorage.getItem("userId");
    let newPosts = [...this.state.posts];
    const idx = newPosts.findIndex((post) => post.id == postId);
    newPosts[idx].likes.push(db.doc(`users/${userId}`));
    db.doc(`posts/${postId}`)
      .update({
        likes: newPosts[idx].likes,
      })
      .then((postRef) => {
        this.setState({ posts: newPosts }, () => console.log(this.state.posts));
      });
  };

  // Affichage Post
  displayPosts(posts) {
    return posts.map((post) => (
      <div className="displayPosts" key={post.id} class="post">

                <div className="infos">
                    <div>
                <img
                    alt="profil picture"
                    src="/logout/avatar.svg"
                />
                </div>

                    {/* <h4>{post.author.login} </h4>
                    */}
                    <div>
                    <h4>{post.author}</h4>
                    <p>{new Date(post.created_at).toLocaleDateString()}</p>
                    </div>
                </div>

                    <div className="container_img_home">
                    <img className="imageForm" src={post.picture_url} />
                    </div>
        <div className="likes">
          {this.isLiked(post) ? (
            <img
              id={post.id}
              className="imgLike"
              src="/heart_full.svg"
              onClick={this.handleDislike}
            />
          ) : (
            <img
              id={post.id}
              className="imgLike"
              src="/heart.svg"
              onClick={this.handleLike}
            />
          )}
          <p>{post.likes.length}</p>
        </div>

        {/* <p>{post.challenge_ref.duration} </p> 
        <div> {post.challenge_ref.description} </div>*/}
      </div>

    ));
  }

  render() {
    const { posts } = this.state;
    console.log(posts);
    return (
      <div className="home">
        <div className="header">
          <Link to="/profil">
            {" "}
            <img
              alt="profil picture"
              className="avatar"
              src="/logout/avatar.svg"
            />{" "}
          </Link>
          <Link to="/login">
            <button
              onClick={() => firebase.auth().signOut()}
              className="logout"
            ></button>
          </Link>
        </div>

        <div className="en-tete">
          <h2>DÃ©couvrir</h2>
        </div>

        {posts.length == 0 ? (
          <p> Aucune publication </p>
        ) : (
            <div class="publications_on_home">
          {this.displayPosts(posts)}
          </div>
        )}

        <nav className="nav_menu">
          <Link to="/home">
            <div className="img1_b"></div>
          </Link>
          <Link to="/post">
            <div className="img2"></div>
          </Link>
          <Link to="/subscription">
            <div className="img3"></div>
          </Link>
          <Link to="/defi">
            <div className="img4"></div>
          </Link>
        </nav>
      </div>
    );
  }
}
